    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        // takes the color selected as a string and uses is to change the color of the ball
        String selectedValue1 = jComboBox1.getSelectedItem().toString();
        if("GREEN".equals(selectedValue1)){
            ballColor = Color.GREEN;
            
            //colorChoice[1] = 0;
        }
        if("YELLOW".equals(selectedValue1)){
            ballColor = Color.YELLOW;
            //colorChoice[1] = 1;
        }
        if("RED".equals(selectedValue1)){
            ballColor = Color.RED;
            //colorChoice[1] = 2;
        }
        if("WHITE".equals(selectedValue1)){
            ballColor =Color.WHITE;
            //colorChoice[1] = 3;
        }
    }//GEN-LAST:event_jComboBox1ActionPerformed

