    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
       
       SinglePlay2 gamePlay1 = new SinglePlay2(ballColor, paddleColor);  //creating an object of class gameplay extending jpanel 
       JFrame obj = new JFrame();
       obj.setBounds(20, 20, 1000, 800);
       obj.setTitle("Brick Breaker");
       obj.setResizable(false);  //making the window unresizable
       obj.setVisible(true);
       obj.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
       this.dispose();
       obj.add(gamePlay1);   //adding the created object to the jframe object for display
    }//GEN-LAST:event_jButton1MouseClicked

